      /*科目*/A030306/*收入*/,
      /*期间*/YearDefault/*全年*/,
      /*情景*/S03/*预计*/, 
      /*年*/&PriorYear,
      /*版本*/V04/*目标核定*/,
      /*项目*/PJ00/*项目缺省*/, 
      /*专业*/BU00/*专业缺省*/, 
      /*综合1*/SP1Biz00/*经营类综合1缺省*/,
      /*综合2*/SP2Biz00/*经营类综合2缺省*/)

      @IDescendants(TotalProduct);
		
ENDFIX
FIX ( /*组织*/@IChildren("00000100-P")/*中国邮政集团公司*/,"21000100",
      /*科目*/A030306/*收入*/,
      /*期间*/YearDefault/*全年*/,
      /*情景*/S01/*预算*/,S03/*预计*/, 
      /*年*/&PriorYear,&BudgetYear,
      /*版本*/V04/*目标核定*/,
      /*项目*/PJ00/*项目缺省*/, 
      /*专业*/BU00/*专业缺省*/, 
      /*综合1*/SP1Biz00/*经营类综合1缺省*/,
      /*综合2*/SP2Biz00/*经营类综合2缺省*/)

      @IDescendants(TotalProduct);
		
ENDFIX
/*组织聚合*/
FIX ( /*组织*/
      /*科目*/A030306/*收入*/,
      /*期间*/YearDefault/*全年*/,
      /*情景*/S01/*预算*/,S03/*预计*/, 
      /*年*/&PriorYear,&BudgetYear,
      /*版本*/V04/*目标核定*/,
      /*项目*/PJ00/*项目缺省*/, 
      /*产品*/@IDescendants(P01),
      /*专业*/BU00/*专业缺省*/, 
      /*综合1*/SP1Biz00/*经营类综合1缺省*/,
      /*综合2*/SP2Biz00/*经营类综合2缺省*/)
     
      @IChildren("00000100-P")/*中国邮政集团公司*/;

ENDFIX


FIX ( /*组织*/@Children("00000100-P"),"21000100-P"/*中国邮政集团公司江苏省分公司*/,"21000100",
      /*情景*/
      /*科目*/A030306/*收入*/,
      /*期间*/YearDefault/*全年*/,
      /*版本*/V04/*目标核定*/, 
      /*产品*/@IDescendants(P01), 
      /*专业*/BU00/*专业缺省*/, 
      /*综合1*/SP1Biz00/*经营类综合1缺省*/,
      /*综合2*/SP2Biz00/*经营类综合2缺省*/)
    FIX ( /*年*/&BudgetYear)
        S23 = ( /*预算*/S01 - &PriorYear->S03/*"预计"*/);
        S05 = ( /*预算*/S01 - &PriorYear->S03/*"预计"*/) / @ABS(&PriorYear->S03/*"预计"*/);
    ENDFIX
    FIX ( /*年*/ &PriorYear)
        S23 = ( S03 /*"预计"*/- V0299/*实际版*/->&PriorYear1->S02) ;
        S04 = ( S03 /*"预计"*/- V0299/*实际版*/->&PriorYear1->S02) / @ABS(V0299/*实际版*/->&PriorYear1->/*"实际"*/S02);
    ENDFIX
ENDFIX

SET UPDATECALC OFF;
SET AGGMISSG ON;
FIX (@IChildren ( {CurEntity}) and @Relative( AllEntity, 0), E00)
%Script(name:="10.11预实对比-收入明细（展示表）",application:="HPYW01",plantype:="Plan1")
ENDFIX

