/*
*
* 作者:赵令剑;* 创建时间:2015/7/15;
* 脚本描述:2.21经营类标杆表（输入表）_标杆发布
* 修改历史:
*
*
*/
SET UPDATECALC OFF;
SET AGGMISSG ON;
FIX ( /*科目*/{BiaoGan}/*经营预算类标杆,资金预算类标杆*/, @IDescendants( {CurEntity}), YearDefault/*全年*/, S01/*预算*/, &BudgetYear, /*项目*/PJ00/*项目缺省*/, &TgtVer/*目标版*/, /*综合1*/SP1Biz00/*经营类综合1缺省*/, /*综合2*/SP2Biz00/*经营类综合2缺省*/)
    /*产品*/
    P00 /*产品缺省*/(
        IF ( @GEN( @Currmbr( Entity_HP_YW)) == 3 or @GEN( @Currmbr( Entity_HP_YW)) == 4)
            BG04 /*本级预算标杆值*/= BG07 /*集团下达省级预算标杆值*/+ BG08/*省级调整值*/;
        ELSEIF ( @GEN( @Currmbr( Entity_HP_YW)) == 5)
            BG04 /*本级预算标杆值*/= BG10 /*省下达市级预算标杆值*/+ BG11/*市级调整值*/;
        ELSEIF ( @GEN( @Currmbr( Entity_HP_YW)) == 6)
            BG04 /*本级预算标杆值*/= BG12 /*市级下达区县预算标杆值*/+ BG13/*区县调整值*/;
        ELSEIF ( @GEN( @Currmbr( Entity_HP_YW)) == 7 or @GEN(@Currmbr( Entity_HP_YW)) == 8 or @GEN(@Currmbr( Entity_HP_YW)) == 9 or @GEN(@Currmbr( Entity_HP_YW)) == 10)
            BG04 /*本级预算标杆值*/= BG14 /*区县下达网点预算标杆值*/+ BG15/*网点调整值*/;
        ENDIF
    )
ENDFIX
FIX ( /*科目*/{BiaoGan}, @IDescendants("00000100-P"/*中国邮政集团*/), E00, &BudgetYear, YearDefault/*全年*/, S01/*预算*/, /*产品*/P00/*产品缺省*/, /*项目*/PJ00/*项目缺省*/, &TgtVer/*目标版*/, /*综合1*/SP1Biz00/*经营类综合1缺省*/, /*综合2*/SP2Biz00/*经营类综合2缺省*/)
    BG03 /*上级发布预算标杆值*/= @Parent(@Currmbr(Entity_HP_YW))->BG04/*本级预算标杆值*/;
ENDFIX
FIX ( /*科目*//*组织*/@IDescendants( {CurEntity})/*当前实体*/, /*年*/&BudgetYear/*分析年度*/, /*期间*/YearDefault/*全年*/, /*版本*//*情景*/S01/*预算*/, /*专业*//*产品*/P00/*产品缺省*/, /*项目*/PJ00/*项目缺省*/, /*综合1*/SP1Biz00/*经营类综合1缺省*/, /*综合2*/SP2Biz00/*经营类综合2缺省*/)
    FIX ( /*专业*/BG03/*上级发布预算标杆值*/, /*专业*/BG04/*本级预算标杆值*/, /*科目*/{BiaoGan}/*经营预算类标杆,资金预算类标杆*/)
        /*版本*/
        V0299 /*最终版*/= /*版本*/&TgtVer/*目标版*/;
    ENDFIX
