(
/*科目*/
/*组织*/{CurEntity}/*当前实体*/,
/*年*/
/*期间*/YearDefault/*全年*/,
/*版本*/V0299/*最终版*/,
/*情景*/S01/*预算*/,
/*专业*/BU00/*专业缺省*/,
/*产品*/P00/*产品缺省*/,
/*项目*/PJ00/*项目缺省*/,
/*综合1*/
/*综合2*/SP2Biz00/*经营类综合2缺省*/
)
FIX(/*科目*/@Descendants(A030101)/*基础数据_经营*/,/*年*/&BudgetYear/*2015年*/)
/*综合1*/SP1Biz02/*增长率*/ = (/*综合1*/SP1Biz01/*经营类数额*/ - /*年*/&PriorYear/*2014年*/->/*综合1*/SP1Biz01/*经营类数额*/) / /*年*/&PriorYear/*2014年*/->/*综合1*/SP1Biz01/*经营类数额*/;
ENDFIX
ENDFIX
SET CREATENONMISSINGBLK OFF;

