FIX ( /*科目*/A030306/*收入*/, /*组织*/@Remove(@IDescendants( AllEntity),@Relative(AllEntity,0)),/*年*/&CurYear/*当前年*/, /*期间*/&BudgetMonth/*预算月份*/, /*版本*/V0299/*最终版*/, /*情景*//*专业*/BU00/*专业缺省*/, /*产品*//*项目*/PJ00/*项目缺省*/, /*综合1*//*综合2*/SP2Biz00/*经营类综合2缺省*/) 
    FIX ( /*综合1*/SP1Biz0303/*服务*/, /*产品*/@IDescendants( TotalProduct)/*产品汇总*/, @IDescendants( Express/*结算收入*/)) 
        S20 /*预计累计完成进度*/= ( S01 /*预算*/+ &CurMonth/*当前月*/->S09/*累计实际*/) / YearDefault/*全年*/->S01/*预算*/;
    ENDFIX 
    FIX ( /*综合1*/SP1Biz0304/*国内*/, /*产品*/@IDescendants( P0101)/*国内函件服务*/) 
        S20 /*预计累计完成进度*/= ( S01 /*预算*/+ &CurMonth/*当前月*/->S09/*累计实际*/) / YearDefault/*全年*/->S01/*预算*/;
    ENDFIX 
    FIX ( /*综合1*/SP1Biz0305/*国际*/, /*产品*/@IDescendants( P0102)/*国际函件服务*/) 
        S20 /*预计累计完成进度*/= ( S01 /*预算*/+ &CurMonth/*当前月*/->S09/*累计实际*/) / YearDefault/*全年*/->S01/*预算*/;
    ENDFIX 
    FIX ( /*综合1*/SP1Biz0306/*发报刊*/, /*产品*/@IDescendants( P0301)/*报刊发行*/) 
        S20 /*预计累计完成进度*/= ( S01 /*预算*/+ &CurMonth/*当前月*/->S09/*累计实际*/) / YearDefault/*全年*/->S01/*预算*/;
    ENDFIX 
    FIX ( /*综合1*/SP1Biz0307/*订销*/, /*产品*/@IDescendants( P0302)/*报刊订阅*/) 
        S20 /*预计累计完成进度*/= ( S01 /*预算*/+ &CurMonth/*当前月*/->S09/*累计实际*/) / YearDefault/*全年*/->S01/*预算*/;
