FIX ( /*科目*/A030306/*收入*/, /*组织*/ /*年*/&CurYear/*当前年*/, /*期间*/&CurMonth/*当前月份*/,/*版本*/V0299/*最终版*/, /*情景*//*专业*/BU00/*专业缺省*/, /*产品*//*项目*/PJ00/*项目缺省*/, /*综合1*//*综合2*/SP2Biz00/*经营类综合2缺省*/)
    FIX ( /*综合1*/SP1Biz0303/*服务*/, /*产品*/@IDescendants( TotalProduct)/*产品汇总*/, EP02/*物流产品（结算）*/)
        S02 /*实际*/= S13 /*账面数*/+ V0294/*清分版*/->S14/*清分*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0304/*国内*/, /*产品*/@IDescendants( P0101)/*国内函件服务*/)
