    ENDFIX
    FIX ( /*综合1*/SP1Biz0305/*国际*/, /*产品*/@IDescendants( P0102)/*国际函件服务*/)
        S02 /*实际*/= S13 /*账面数*/+ V0294/*清分版*/->S14/*清分*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0306/*发报刊*/, /*产品*/@IDescendants( P0301)/*报刊发行*/)
        S02 /*实际*/= S13 /*账面数*/+ V0294/*清分版*/->S14/*清分*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0307/*订销*/, /*产品*/@IDescendants( P0302)/*报刊订阅*/)
        S02 /*实际*/= S13 /*账面数*/+ V0294/*清分版*/->S14/*清分*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0301/*经销*/, /*产品*/@IDescendants( P11)/*销售商品*/)
        S02 /*实际*/= S13 /*账面数*/+ V0294/*清分版*/->S14/*清分*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0302/*代销*/, /*产品*/@IDescendants( P17)/*代理商品*/)
        S02 /*实际*/= S13 /*账面数*/+ V0294/*清分版*/->S14/*清分*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0303/*服务*/, /*产品*/@IDescendants(EP01),@IDescendants(EP02)/*速递物流结算、内部结算*/, TotalProduct/*产品汇总*/)
        S02 /*实际*/= S13 /*账面数*/+ V0294/*清分版*/->S14/*清分*/;
    ENDFIX
ENDFIX
