FIX ( /*科目*/A030306/*收入*/, /*组织*//*年*/&CurYear/*当前年*/, /*期间*/&BudgetMonth/*预算月份*/, /*版本*/V0299/*最终版*/, /*情景*/S01/*预算*/, /*专业*/BU00/*专业缺省*/, /*产品*//*项目*/PJ00/*项目缺省*/, /*综合1*//*综合2*/SP2Biz00/*经营类综合2缺省*/)
    FIX ( /*综合1*/SP1Biz0304/*国内*/)
        /*产品*/
        @IDescendants( P0101)/*国内函件服务*/;
    ENDFIX
    FIX ( @Remove( @Relative( P0101/*国内函件服务*/, 0), @List(P010101/*函件*/,P010102/*印刷品*/, P010103/*邮资封片卡*/, P010107/*其他函件*/)))
        SP1Biz0303 /*服务*/= SP1Biz0304/*国内*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0305/*国际*/)
        /*产品*/
        @IDescendants( P0102)/*国际函件服务*/;
    ENDFIX
    FIX ( SP1Biz0303/*服务*/)
        /*产品*/
        P010101 /*函件*/= ( P010101/*函件*/->SP1Biz0304 /*国内*/+ P010101/*函件*/->SP1Biz0305/*国际*/) / 2;
        P010102 /*函件*/= ( P010102/*函件*/->SP1Biz0304 /*国内*/+ P010102/*函件*/->SP1Biz0305/*国际*/) / 2;
        P010103 /*邮资封片卡*/= ( P010103/*邮资封片卡*/->SP1Biz0304 /*国内*/+ P010103/*邮资封片卡*/->SP1Biz0305/*国际*/) / 2;
        P010202 /*国际小包*/= P010202/*国际小包*/->SP1Biz0305/*国际*/;
        P010107 /*其他函件*/= ( P010107/*其他函件*/->SP1Biz0304 /*国内*/+ P010107/*其他函件*/->SP1Biz0305/*国际*/) / 2;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0303/*服务*/)
        /*产品*/
        @IDescendants( P01/*函件服务*/);
    ENDFIX
    FIX ( /*综合1*/SP1Biz0303/*服务*/)
        /*产品*/
        @IDescendants( P02)/*包裹服务*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0306/*发报刊*/)
        /*产品*/
        @IDescendants( P0301)/*报刊发行*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0307/*订销*/)
        /*产品*/
        @IDescendants( P0302)/*报刊订阅*/;
    ENDFIX
    FIX ( @Relative( P0301, 0)/*报刊发行*/)
        SP1Biz0303 /*服务*/= ( SP1Biz0307 /*订销*/+ SP1Biz0306/*发报刊*/) / 2;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0303/*服务*/)
        /*产品*/
        @IDescendants( P0303)/*报刊零售*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0303/*服务*/)
        /*产品*/
        @IDescendants( P03/*报刊*/);
    ENDFIX
    FIX ( /*综合1*/SP1Biz0303/*服务*/)
        /*产品*/
        @IDescendants( P05)/*集邮*/;
        @IDescendants( P06)/*汇兑业务*/;
        @IDescendants( P07)/*代理金融*/;
        @IDescendants( P09)/*代理服务*/;
        @IDescendants( P10)/*分销服务*/;
        @IDescendants( P14)/*信息服务*/;
        @IDescendants( P15)/*媒体服务*/;
        @IDescendants( P16)/*其他业务*/;
        @IDescendants( P08)/*快递服务*/;
        @IDescendants( P18)/*邮票发行*/;
        @IDescendants( P19)/*物流服务*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0303/*服务*/)
        /*产品*/
        @IDescendants( Express/*结算收入*/);
    ENDFIX
    FIX ( /*综合1*/SP1Biz0301/*经销*/)
        /*产品*/
        @IDescendants( P11)/*销售商品*/;
    ENDFIX
