        SP1Biz0303 /*服务*/= SP1Biz0301/*经销*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0302/*代销*/)
        /*产品*/
        @IDescendants( P17)/*代理商品*/;
    ENDFIX
    FIX ( @Relative( P17, 0)/*代理商品*/)
        SP1Biz0303 /*服务*/= ( SP1Biz0301 /*经销*/+ SP1Biz0302/*代销*/) / 2;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0303/*服务*/)
        /*产品*/
        @IDescendants( TotalProduct/*产品汇总*/);
        TotalProduct /*产品汇总*/= TotalProduct /*产品汇总*/+ Express/*结算收入*/;
    ENDFIX
