    ENDFIX
    FIX ( /*综合1*/SP1Biz0306/*发报刊*/, @IDescendants( P0301)/*报刊发行*/)
        S20 /*预计全年预算进度*/= ( S01 /*预算*/+ &CurMonth/*当前月*/->S09/*累计实际*/) / YearDefault/*全年*/->S01/*预算*/;
    ENDFIX
    FIX ( /*综合1*/SP1Biz0307/*订销*/, @IDescendants( P0302)/*报刊订阅*/)
