    ENDFIX
    FIX ( /*综合1*/SP1Biz0301/*经销*/, @IDescendants( P11)/*销售商品*/)
        S20 /*预计全年预算进度*/= ( S01 /*预算*/+ &CurMonth/*当前月*/->S09/*累计实际*/) / YearDefault/*全年*/->S01/*预算*/;
    ENDFIX
